using SFML.Graphics;
using SFML.Window;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using SFML.System;

public class Mover : Component, IMove
{
    public enum Direction
    {
        Rigth,
        Left,
        Up,
        Down,
        None
    }

    public Vector2f Speed;
    protected Vector2f position;
    protected Vector2f movement;
    protected Direction direction;
    public Vector2f Position { get { return position; } }

    public Mover()
    {
        this.enabled = true;
    }

    public Mover(Vector2f position, Vector2f speed) : base()
    {
        this.enabled = true;
        this.position = position;
        this.Speed = speed;
    }


    public void SetMoveDirection(Direction direction)
    {
        this.direction = direction;
    }

    public override void Update(float deltaTime)
    {
        if(this.enabled)
        {
            Move(deltaTime);
        }
    }

    private void Move(float deltaTime)
    {
        this.movement = new Vector2f();

        if(this.direction == Direction.None)
            return;

        switch(this.direction)
        {
            case Direction.Down:
                this.movement.Y = 1;
                break;
            case Direction.Up:
                this.movement.Y = -1;
                break;
            case Direction.Rigth:
                this.movement.X = 1;
                break;
            case Direction.Left:
                this.movement.X = -1;
                break;
            default:
                break;
        }

        this.movement.X *= Speed.X * deltaTime;
        this.movement.Y *= Speed.Y * deltaTime;
        Console.WriteLine(this.movement.ToString());
        position += this.movement;
    }
}
